import React from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import Particles from 'react-particles-js';

const particlesOptions = {
  particles: {
    number: {
      value: 100,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
};

const initialState = {
  input: '',
  imageUrl: '',
  box: {},
  route: 'signin',
  isSignedIn: false,
  user: {
    'id': '',
    'name': '',
    'email': '',
    'entries': 0,
    'joined': ''
  }
}

class App extends React.Component {

  constructor() {
    super();
    this.state = initialState;
  }

  loadUser = (data) => {
    this.setState({
      user: {
        'id': data.id,
        'name': data.name,
        'email': data.email,
        'entries': data.entries,
        'joined': data.joined
      }
    });
  }

  // componentDidMount() {
  //   fetch('http://localhost:3000')
  //     .then(response => response.json())
  //     .then(data => console.log(data));
  // }

  calculateFaceLocation = (data) => {
    const clarifariFace = data.outputs[0].data.regions[0].region_info.bounding_box;

    //do DOM manipulation
    const image = document.getElementById('inputImage');
    const width = Number(image.width);
    const height = Number(image.height);
    //console.log(width);
    //console.log(height);

    return {
      leftCol: clarifariFace.left_col * width,
      topRow: clarifariFace.top_row * height,
      rightCol: width - (clarifariFace.right_col * width),
      bottomRow: height - (clarifariFace.bottom_row * height),
    }
  }

  displayFaceBox = (box) => {
    console.log(box);
    this.setState({
      box: box
    });
  }

  onInputChange = (event) => {
    //console.log(event.target.value);
    this.setState({
      input: event.target.value
    })
  }

  onImageSubmit = () => {
    this.setState({
      imageUrl: this.state.input
    });
    //console.log('click');
    fetch('https://cryptic-everglades-77116.herokuapp.com/imageurl', {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        input: this.state.input
      })
    })
    .then(response => response.json())
    .then(response => {
      if (response) {
        fetch('https://cryptic-everglades-77116.herokuapp.com/image', {
          method: 'put',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            id: this.state.user.id
          })
        })
          .then(response => response.json())
          .then(count => {
            this.setState(Object.assign(this.state.user, {
              entries: count
            }
            ))
          }).catch(console.log);
      }
      this.displayFaceBox(this.calculateFaceLocation(response))
    }
    ).catch(error => console.log(error));
  }

  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState(initialState);
    } else if (route === 'home') {
      this.setState({
        isSignedIn: true
      });
    }
    this.setState({
      route: route
    });
  }

  render() {
    return (
      <div className="App">
        <Particles className='particles'
          params={particlesOptions}
        />
        <Navigation isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange} />
        {
          this.state.route === 'home' ?
            <div>
              <Logo />
              <Rank name={this.state.user.name} entries={this.state.user.entries} />
              <ImageLinkForm
                onInputChange={this.onInputChange}
                onButtonSubmit={this.onImageSubmit} />
              <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl} />
            </div> :
            (this.state.route === 'signin' ?
              <SignIn loadUser={this.loadUser} onRouteChange={this.onRouteChange} /> :
              <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange} />
            )
        }
      </div>
    );
  }
}

export default App;
